mapper.select:
SELECT T1.ID, T1.REQUEST_URI, T1.REQUEST_IP, T1.SYS_USER_ID, T1.SYS_USER_ACCOUNT, T1.OPERATE_TYPE, T1.MODULE_NAME,T1.OPERATION_DESC, T1.MODULE_CODE,T1.METHOD_NAME,T1.METHOD_CODE,T1.OPERATE_RESULT,T1.LOG_CONTENT,T1.OPERATE_TIME,T1.CREATE_TIME,T1.MENU_NAME,T1.MODULE_CONTENT, T1.DEL_FLAG,T2.NAME AS USER_NAME FROM T_SYS_LOG T1 LEFT JOIN T_SYS_USER T2 ON T1.SYS_USER_ID = T2.ID WHERE T1.DEL_FLAG = 0
AND T1.SYS_USER_ACCOUNT=#{searchParams.sysUserAccount,jdbcType=VARCHAR}
AND DATE_FORMAT(T1.OPERATE_TIME,'%Y-%m-%d') >= DATE_FORMAT(#{searchParams.operateTimeBefore, jdbcType=TIMESTAMP},'%Y-%m-%d')
AND DATE_FORMAT(T1.OPERATE_TIME,'%Y-%m-%d') <= DATE_FORMAT(#{searchParams.operateTimeAfter, jdbcType=TIMESTAMP},'%Y-%m-%d')
ORDER BY T1.OPERATE_TIME DESC LIMIT #{offset,jdbcType=BIGINT},#{limit,jdbcType=BIGINT}

SELECT
COUNT(0)
FROM
  T_SYS_LOG
WHERE DEL_FLAG = 0
  AND SYS_USER_ACCOUNT=#{sysUserAccount,jdbcType=VARCHAR}
   AND OPERATE_TIME >= #{operateTimeBefore, jdbcType=TIMESTAMP}
   AND OPERATE_TIME <= #{operateTimeAfter, jdbcType=TIMESTAMP}
