UPDATE T_ROLE
ROLE_NAME = #{roleName, jdbcType=VARCHAR}, ROLE_CODE = #{roleCode, jdbcType=VARCHAR}, ENABLED = #{enabled, jdbcType=TINYINT}, DESCRIPTION = #{description, jdbcType=VARCHAR}, ROLE_MENUS = #{roleMenus, jdbcType=VARCHAR}, LAST_UPDATE_TIME = NOW()
WHERE DEL_FLAG = 0 AND ENABLED = 1 AND ID = #{id, jdbcType=BIGINT}
